import React from 'react';
import { BrowserRouter, Routes, Route, useLocation } from 'react-router-dom';

// Import Layouts
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import AdminLayout from './admin/AdminLayout';

// Import User Pages
import Home from './components/Home';
import Register from './components/Register';
import Login from './components/Login';
import Search from './components/Search';
import RequestBlood from './components/RequestBlood';
import Profile from './components/Profile';
import WhyDonate from './components/WhyDonate';
import About from './components/About';
import Contact from './components/Contact';

// Import Admin Pages
import AdminLogin from './admin/AdminLogin';
import Dashboard from './admin/Dashboard'; // We will create this new component
import ManageDonors from './admin/ManageDonors';
import ManageRequests from './admin/ManageRequests';
import StockReport from './admin/StockReport';

// This is a helper component to manage which layout to display
function PageLayout() {
  const location = useLocation();
  const isAdminRoute = location.pathname.startsWith('/admin/');
  const isAdminLoginRoute = location.pathname === '/admin/login';

  if (isAdminLoginRoute) {
    return <AdminLogin />;
  }

  if (isAdminRoute) {
    // For admin pages, the AdminLayout will render the specific page component
    return (
      <AdminLayout>
        <Routes>
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/donors" element={<ManageDonors />} />
          <Route path="/requests" element={<ManageRequests />} />
          <Route path="/stock" element={<StockReport />} />
        </Routes>
      </AdminLayout>
    );
  } else {
    // For user pages, we render the Navbar, the page content, and the Footer
    return (
      <>
        <Navbar />
        <main className="container py-4">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/signup" element={<Register />} />
            <Route path="/login" element={<Login />} />
            <Route path="/search" element={<Search />} />
            <Route path="/request-blood" element={<RequestBlood />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/why-donate" element={<WhyDonate />} />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <Footer />
      </>
    );
  }
}

function App() {
  return (
    <BrowserRouter>
      <PageLayout />
    </BrowserRouter>
  );
}

export default App;
