import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Register() {
  const [formData, setFormData] = useState({
    name: '', email: '', password: '', age: '', blood_group: '',
    gender: '', mobile: '', address: '',
  });
  const navigate = useNavigate();

  // This function updates the state as the user types
  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  // This function handles the form submission
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevents the browser from reloading the page
    try {
      const response = await fetch('http://localhost:5000/api/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      alert(data.message);
      if (response.ok) navigate('/login'); // Redirect to login page on success
    } catch (error) {
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <div>
      <h2>Donor Signup</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Full Name" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
        <select name="blood_group" onChange={handleChange} required>
          <option value="">Select Blood Group</option>
          <option>A+</option><option>A-</option><option>B+</option><option>B-</option>
          <option>AB+</option><option>AB-</option><option>O+</option><option>O-</option>
        </select>
        <input type="text" name="gender" placeholder="Gender" onChange={handleChange} required />
        <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} required />
        <input type="text" name="address" placeholder="Address (City)" onChange={handleChange} required />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
}
export default Register;
