// src/admin/ManageDonors.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const API_BASE_URL = 'http://localhost:5000';

function ManageDonors() {
  const [donors, setDonors] = useState([]);
  const [form, setForm] = useState({
    name: '',
    email: '',
    blood_group: '',
    mobile: '',
    address: '',
  });
  const navigate = useNavigate();

  const fetchDonors = async () => {
    const res = await fetch(`${API_BASE_URL}/api/admin/donors`);
    const data = await res.json();
    setDonors(data);
  };

  useEffect(() => {
    if (localStorage.getItem('isAdmin') !== 'true') navigate('/admin/login');
    else fetchDonors();
  }, [navigate]);

  const handleChange = (e) => {
    setForm((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleAddDonor = async (e) => {
    e.preventDefault();
    if (!form.name || !form.email || !form.blood_group) {
      alert('Name, Email and Blood Group are required');
      return;
    }

    await fetch(`${API_BASE_URL}/api/admin/donors`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    });

    setForm({
      name: '',
      email: '',
      blood_group: '',
      mobile: '',
      address: '',
    });
    fetchDonors();
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this donor?')) return;
    await fetch(`${API_BASE_URL}/api/admin/donors/${id}`, {
      method: 'DELETE',
    });
    fetchDonors();
  };

  return (
    <div>
      <h1 className="h2 mb-4">Manage Donors</h1>

      {/* Add donor form */}
      <div className="card mb-4 shadow-sm">
        <div className="card-body">
          <h5 className="card-title mb-3">Add New Donor</h5>
          <form className="row g-3" onSubmit={handleAddDonor}>
            <div className="col-md-4">
              <label className="form-label">Name</label>
              <input
                name="name"
                className="form-control"
                value={form.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-md-4">
              <label className="form-label">Email</label>
              <input
                type="email"
                name="email"
                className="form-control"
                value={form.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-md-2">
              <label className="form-label">Blood Group</label>
              <select
                name="blood_group"
                className="form-select"
                value={form.blood_group}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                {['A+', 'A-', 'B+', 'B-', 'AB+', 'AB-', 'O+', 'O-'].map((bg) => (
                  <option key={bg} value={bg}>
                    {bg}
                  </option>
                ))}
              </select>
            </div>
            <div className="col-md-2">
              <label className="form-label">Mobile</label>
              <input
                name="mobile"
                className="form-control"
                value={form.mobile}
                onChange={handleChange}
              />
            </div>
            <div className="col-md-8">
              <label className="form-label">Address</label>
              <input
                name="address"
                className="form-control"
                value={form.address}
                onChange={handleChange}
              />
            </div>
            <div className="col-12 text-end">
              <button type="submit" className="btn btn-primary">
                Add New Donor
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Donors table */}
      <div className="card shadow-sm">
        <div className="card-body">
          <table className="table table-hover">
            <thead>
              <tr>
                <th>#</th>
                <th>Name</th>
                <th>Email</th>
                <th>Blood Group</th>
                <th>Mobile</th>
                <th>Address</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {donors.map((d, idx) => (
                <tr key={d._id}>
                  <td>{idx + 1}</td>
                  <td>{d.name}</td>
                  <td>{d.email}</td>
                  <td>
                    <span className="badge bg-danger">{d.blood_group}</span>
                  </td>
                  <td>{d.mobile}</td>
                  <td>
                    {d.address ||
                      d.address_line ||
                      `${d.city || ''} ${d.state || ''}`}
                  </td>
                  <td>
                    <button
                      className="btn btn-sm btn-danger"
                      onClick={() => handleDelete(d._id)}
                    >
                      <i className="fas fa-trash-alt me-1"></i>Delete
                    </button>
                  </td>
                </tr>
              ))}
              {donors.length === 0 && (
                <tr>
                  <td colSpan="7" className="text-center">
                    No donors found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ManageDonors;
