import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Profile() {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  // useEffect runs automatically when the component first loads
  useEffect(() => {
    if (!userId) {
      alert("Please log in to view your profile.");
      navigate('/login');
      return;
    }
    const fetchProfile = async () => {
      const response = await fetch(`http://localhost:5000/api/profile/${userId}`);
      const data = await response.json();
      setProfile(data); // Save the fetched data to state
    };
    fetchProfile();
  }, [userId, navigate]); // The effect depends on userId and navigate

  if (!profile) return <div>Loading profile...</div>; // Show a loading message until data is fetched

  return (
    <div>
      <h2>My Profile</h2>
      {profile.user && (
        <div>
          <h3>Personal Information</h3>
          <p><strong>Name:</strong> {profile.user.name}</p>
          <p><strong>Email:</strong> {profile.user.email}</p>
          <p><strong>Blood Group:</strong> {profile.user.blood_group}</p>
          <p><strong>Address:</strong> {profile.user.address}</p>
        </div>
      )}
      <h3>Donation History</h3>
      <table>
        <thead><tr><th>Venue</th><th>Date</th><th>Units</th></tr></thead>
        <tbody>
          {profile.history.length > 0 ? profile.history.map(h => (
            <tr key={h._id}><td>{h.venue}</td><td>{new Date(h.donation_date).toLocaleDateString()}</td><td>{h.units}</td></tr>
          )) : <tr><td colSpan="3">No donation history found.</td></tr>}
        </tbody>
      </table>
    </div>
  );
}
export default Profile;
