import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ManageDonors() {
  const [donors, setDonors] = useState([]);
  const navigate = useNavigate();

  // This function fetches all donor data from the backend
  const fetchDonors = async () => {
    const response = await fetch('http://localhost:5000/api/admin/donors');
    const data = await response.json();
    setDonors(data);
  };

  // useEffect runs when the component loads to check for admin login and fetch data
  useEffect(() => {
    if (localStorage.getItem('isAdmin') !== 'true') {
      alert("You must be logged in as an admin to view this page.");
      navigate('/admin/login');
    } else {
      fetchDonors();
    }
  }, [navigate]);

  const handleDelete = async (donorId) => {
      if (window.confirm("Are you sure you want to delete this donor?")) {
          await fetch(`http://localhost:5000/api/admin/donors/${donorId}`, { method: 'DELETE' });
          fetchDonors(); // Refresh the list after deleting
      }
  };

  return (
    <div>
      <h2>Manage Donors</h2>
      <table>
        <thead><tr><th>Name</th><th>Email</th><th>Blood Group</th><th>Action</th></tr></thead>
        <tbody>
          {donors.map((donor) => (
            <tr key={donor._id}>
              <td>{donor.name}</td><td>{donor.email}</td><td>{donor.blood_group}</td>
              <td><button onClick={() => handleDelete(donor._id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
export default ManageDonors;
