import React, { useState } from 'react';

function Search() {
  const [bloodGroup, setBloodGroup] = useState('');
  const [address, setAddress] = useState('');
  const [donors, setDonors] = useState([]); // This state will hold the search results

  const handleSearch = async (e) => {
    e.preventDefault();
    const response = await fetch(`http://localhost:5000/api/search?blood_group=${bloodGroup}&address=${address}`);
    const data = await response.json();
    setDonors(data); // Update the state with the fetched donors
  };

  return (
    <div>
      <h2>Find a Blood Donor</h2>
      <form onSubmit={handleSearch}>
        <select value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)}>
          <option value="">Any Blood Group</option>
          <option>A+</option><option>A-</option><option>B+</option><option>B-</option>
          <option>AB+</option><option>AB-</option><option>O+</option><option>O-</option>
        </select>
        <input type="text" placeholder="City / Address" value={address} onChange={(e) => setAddress(e.target.value)} />
        <button type="submit">Search</button>
      </form>

      <h3>Search Results</h3>
      <table>
        <thead><tr><th>Name</th><th>Blood Group</th><th>Address</th><th>Mobile</th></tr></thead>
        <tbody>
          {donors.map((donor) => (
            <tr key={donor._id}>
              <td>{donor.name}</td><td>{donor.blood_group}</td><td>{donor.address}</td><td>{donor.mobile}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
export default Search;
