import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ManageDonors() {
  const [donors, setDonors] = useState([]);
  const navigate = useNavigate();

  const fetchDonors = async () => {
    const response = await fetch('http://localhost:5000/api/admin/donors');
    const data = await response.json();
    setDonors(data);
  };

  useEffect(() => {
    if (localStorage.getItem('isAdmin') !== 'true') navigate('/admin/login');
    else fetchDonors();
  }, [navigate]);
  
  const handleDelete = async (donorId) => {
      if (window.confirm("Are you sure?")) {
          await fetch(`http://localhost:5000/api/admin/donors/${donorId}`, { method: 'DELETE' });
          fetchDonors();
      }
  };

  return (
    <div>
      <h1 className="h2 mb-4">Manage Donors</h1>
      <div className="card shadow-sm">
        <div className="card-body">
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>#</th><th>Name</th><th>Email</th><th>Blood Group</th><th>Mobile</th><th>Address</th><th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {donors.map((donor, index) => (
                  <tr key={donor._id}>
                    <td>{index + 1}</td>
                    <td>{donor.name}</td>
                    <td>{donor.email}</td>
                    <td><span className="badge bg-danger">{donor.blood_group}</span></td>
                    <td>{donor.mobile}</td>
                    <td>{donor.address}</td>
                    <td><button className="btn btn-sm btn-danger" onClick={() => handleDelete(donor._id)}><i className="fas fa-trash me-1"></i>Delete</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
export default ManageDonors;
