import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      
      if (response.ok) {
        // ASSUMPTION: The API response 'data' includes the user's full name as 'userName'.
        // If your API uses 'name' instead, change data.userName to data.name
        
        // ðŸš¨ CRITICAL CHANGE: Save the user's name to localStorage
        localStorage.setItem('userId', data.userId);
        localStorage.setItem('userName', data.userName); // <-- This is what the Navbar needs

        alert(data.message);
        
        // Use window.location.reload() to force the Navbar component to immediately re-render 
        // and pick up the new localStorage values, if necessary, before navigation.
        window.location.reload(); 
        navigate('/profile');
        
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert('Login failed. Please check your connection.');
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-lg-5 col-md-7">
        <div className="card shadow-sm p-4 p-md-5">
          <div className="text-center mb-4">
            <h2 className="fw-bold">User Login</h2>
            <p className="text-muted">Welcome back to LifeLine.</p>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email Address</label>
              <input type="email" className="form-control" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            </div>
            <div className="d-grid mt-4">
              <button type="submit" className="btn btn-lifeline btn-lg">Login</button>
            </div>
            <div className="text-center mt-3">
              <p className="text-muted mb-2">Don't have an account? <Link to="/signup" className="text-lifeline">Sign Up</Link></p>
              
              <p className="text-muted small"><Link to="/admin/login" className="text-secondary">Login as Admin</Link></p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default Login;
