import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ManageRequests() {
  const [requests, setRequests] = useState([]);
  const navigate = useNavigate();

  const fetchRequests = async () => {
    const response = await fetch('http://localhost:5000/api/admin/requests');
    const data = await response.json();
    setRequests(data);
  };

  useEffect(() => {
    if (localStorage.getItem('isAdmin') !== 'true') navigate('/admin/login');
    else fetchRequests();
  }, [navigate]);

  const handleUpdateStatus = async (requestId, status) => {
      await fetch(`http://localhost:5000/api/admin/requests/${requestId}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ status })
      });
      fetchRequests(); // Refresh the list after updating
  };

  return (
    <div>
      <h2>Manage Blood Requests</h2>
      <table>
        <thead><tr><th>Patient</th><th>Blood Group</th><th>Status</th><th>Action</th></tr></thead>
        <tbody>
          {requests.map((req) => (
            <tr key={req._id}>
              <td>{req.patient_name}</td><td>{req.blood_group}</td><td>{req.status}</td>
              <td>
                <button onClick={() => handleUpdateStatus(req._id, 'approved')}>Approve</button>
                <button onClick={() => handleUpdateStatus(req._id, 'rejected')}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
export default ManageRequests;
