import React, { useState } from 'react';

function Search() {
  const [bloodGroup, setBloodGroup] = useState('');
  const [address, setAddress] = useState('');
  const [donors, setDonors] = useState([]);
  const [searched, setSearched] = useState(false); // To track if a search has been performed

  const handleSearch = async (e) => {
    e.preventDefault();
    const response = await fetch(`http://localhost:5000/api/search?blood_group=${bloodGroup}&address=${address}`);
    const data = await response.json();
    setDonors(data);
    setSearched(true);
  };

  return (
    <div className="text-center">
      <h1 className="fw-bold">Find a Blood Donor</h1>
      <p className="lead text-muted col-md-8 mx-auto">Search for available donors by blood group and location.</p>

      <div className="card shadow-sm p-4 mt-4 col-lg-10 mx-auto">
        <form onSubmit={handleSearch} className="row g-3 align-items-end">
          <div className="col-md-5">
            <label htmlFor="blood_group" className="form-label text-start w-100">Blood Group</label>
            <select className="form-select" id="blood_group" value={bloodGroup} onChange={(e) => setBloodGroup(e.target.value)}>
              <option value="">Any</option>
              <option>A+</option><option>A-</option><option>B+</option><option>B-</option>
              <option>AB+</option><option>AB-</option><option>O+</option><option>O-</option>
            </select>
          </div>
          <div className="col-md-5">
            <label htmlFor="address" className="form-label text-start w-100">City or State</label>
            <input type="text" className="form-control" id="address" placeholder="e.g., Surat" value={address} onChange={(e) => setAddress(e.target.value)} />
          </div>
          <div className="col-md-2 d-grid">
            <button type="submit" className="btn btn-lifeline"><i className="fas fa-search me-1"></i> Search</button>
          </div>
        </form>
      </div>

      <div className="mt-5">
        {!searched && (
          <div className="alert alert-info col-lg-10 mx-auto">Please use the form above to search for donors.</div>
        )}
        {searched && donors.length === 0 && (
          <div className="alert alert-warning col-lg-10 mx-auto">No donors found matching your criteria.</div>
        )}
        {searched && donors.length > 0 && (
           <div className="row g-4">
             {donors.map((donor) => (
                <div className="col-md-6 col-lg-4" key={donor._id}>
                  <div className="card h-100 text-center shadow-sm">
                    <div className="card-body">
                      <div className="mb-2"><span className="badge bg-danger p-2 fs-5">{donor.blood_group}</span></div>
                      <h5 className="card-title fw-bold mt-3">{donor.name}</h5>
                      <p className="card-text text-muted"><i className="fas fa-map-marker-alt me-2"></i>{donor.address}</p>
                      <p className="card-text"><i className="fas fa-phone me-2"></i>{donor.mobile}</p>
                    </div>
                  </div>
                </div>
              ))}
           </div>
        )}
      </div>
    </div>
  );
}
export default Search;
