import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Navbar() {
  // State to manage the logged-in user's name and login status
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState('');
  const navigate = useNavigate();

  // 1. State Management: Check login status on component mount
  useEffect(() => {
    // We assume you store the user's name (e.g., 'Ayush Kothiya') and ID in localStorage upon login
    const storedUserId = localStorage.getItem('userId');
    const storedUserName = localStorage.getItem('userName'); 

    if (storedUserId && storedUserName) {
      setIsLoggedIn(true);
      setUserName(storedUserName);
    } else {
      setIsLoggedIn(false);
      setUserName('');
    }
  }, []);

  // 2. Logout Functionality
  const handleLogout = () => {
    // Clear user data from localStorage
    localStorage.removeItem('userId');
    localStorage.removeItem('userName'); 
    
    // Reset component state
    setIsLoggedIn(false);
    setUserName('');
    
    // Redirect the user to the home or login page
    navigate('/login'); 
  };

  // The dropdown content shown only when logged in
  const loggedInMenu = (
    <div className="nav-item dropdown">
      <a 
        className="nav-link dropdown-toggle d-flex align-items-center" 
        href="#" 
        id="navbarDropdown" 
        role="button" 
        data-bs-toggle="dropdown" 
        aria-expanded="false"
      >
        {/* The 'Hello, Ayush Kothiya' part from the image */}
        <i className="bi bi-person-circle me-1"></i> Hello, {userName.split(' ')[0] || 'User'}
      </a>
      <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="navbarDropdown">
        {/* Link to the profile page */}
        <li><Link className="dropdown-item" to="/profile">My Profile</Link></li>
        
        {/* Logout button */}
        <li><hr className="dropdown-divider" /></li>
        <li><button className="dropdown-item text-danger" onClick={handleLogout}>Logout</button></li>
      </ul>
    </div>
  );

  // The login/signup buttons shown only when logged out
  const loggedOutButtons = (
    <div className="d-flex">
      <Link to="/login" className="btn btn-lifeline-outline me-2">Login</Link>
      <Link to="/signup" className="btn btn-lifeline">Sign Up</Link>
    </div>
  );

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm sticky-top">
      <div className="container">
        <Link className="navbar-brand fw-bold text-lifeline" to="/">
          {/* Using a simple heart icon for compatibility, replace with your 'LifeLine' branding icon */}
          <i className="fas fa-heartbeat me-2"></i>LifeLine
        </Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav mx-auto">
            {/* Navigation links remain the same */}
            <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/why-donate">Why Donate?</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/search">Find Donors</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/request-blood">Request Blood</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/about">About</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
          </ul>
          
          {/* 3. Conditional Rendering: Show dynamic menu or static buttons */}
          {isLoggedIn ? loggedInMenu : loggedOutButtons}
          
        </div>
      </div>
    </nav>
  );
}
export default Navbar;
