import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Profile() {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();
  const userId = localStorage.getItem('userId');

  // --- Helper Functions ---

  // Function to format the date
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    if (dateString.trim() === '') return 'N/A';
    
    try {
        const date = new Date(dateString);
        if (isNaN(date)) return 'N/A';
        return date.toLocaleDateString('en-US', {
            year: 'numeric',
            month: 'short',
            day: 'numeric',
        });
    } catch (e) {
        return 'N/A';
    }
  };

  // Function to determine the last donation date
  const getLastDonationDate = (history) => {
    if (!history || history.length === 0) return 'Never';
    
    const sortedHistory = [...history].sort((a, b) => 
        new Date(b.donation_date) - new Date(a.donation_date)
    );
    return formatDate(sortedHistory[0].donation_date);
  };

  // --- Data Fetching ---
  useEffect(() => {
    if (!userId) {
      alert("Please log in to view your profile.");
      navigate('/login');
      return;
    }
    
    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/profile/${userId}`);
        if (!response.ok) throw new Error('Failed to fetch profile data');

        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };
    fetchProfile();
  }, [userId, navigate]);

  if (!profile || !profile.user) return <div>Loading profile...</div>;

  // --- Data Preparation ---
  const lastDonationDate = getLastDonationDate(profile.history);
  
  // ✅ FIX 1: Combine address using the correct database field name: 'address_line'
  const userAddressParts = [
      profile.user.address_line, // <-- Corrected Field Name
      profile.user.city, 
      profile.user.state, 
      profile.user.pincode
  ];
  // Filters out null/empty strings and joins with ", "
  const fullAddress = userAddressParts.filter(Boolean).join(', ');
  
  // ✅ FIX 2: Using the correct database field name: 'created_at' (lowercase 'c')
  const memberSinceDate = formatDate(profile.user.created_at); // <-- Corrected Field Name

  // --- Rendering the Profile UI ---
  return (
    <div className="container py-4">
      <h2 className="fw-bold mb-4">My Profile</h2>
      <div className="row g-4">
        
        {/* --- Personal Information (Left Column) --- */}
        <div className="col-lg-8">
          <div className="card shadow-sm p-4">
            <div className="d-flex justify-content-between align-items-center mb-4 border-bottom pb-3">
              <h5 className="mb-0 text-muted">Personal Information</h5>
              <button className="btn btn-outline-danger btn-sm" onClick={() => navigate('/edit-profile')}>
                <i className="bi bi-pencil me-1"></i> Edit Profile
              </button>
            </div>
            
            <div className="row g-3">
              {/* Name, Email, Age, Blood Group, Gender, Mobile... */}
              <div className="col-md-6">
                <p className="fw-bold mb-0">Full Name:</p><p>{profile.user.name}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Email Address:</p><p>{profile.user.email}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Age:</p><p>{profile.user.age || 'N/A'}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0 text-danger">Blood Group:</p><p className="fw-bold text-danger">{profile.user.blood_group || 'N/A'}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Gender:</p><p>{profile.user.gender || 'N/A'}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Mobile Number:</p><p>{profile.user.mobile || 'N/A'}</p>
              </div>

              {/* Address (Full Width) - NOW USING CORRECT 'fullAddress' */}
              <div className="col-12 mt-3">
                <p className="fw-bold mb-0">Address:</p>
                <p>{fullAddress || 'Address not set'}</p>
                <Link 
                  to={fullAddress ? `https://maps.google.com/?q=${encodeURIComponent(fullAddress)}` : '#'} 
                  target="_blank" 
                  className="btn btn-outline-secondary btn-sm"
                  disabled={!fullAddress}
                >
                  View on Map
                </Link>
              </div>
              
              {/* Last Donation Date and Member Since - NOW USING CORRECT 'memberSinceDate' */}
              <div className="col-md-6 mt-3">
                <p className="fw-bold mb-0">Last Donation Date:</p>
                <p>{lastDonationDate}</p>
              </div>
              <div className="col-md-6 mt-3">
                <p className="fw-bold mb-0">Member Since:</p>
                <p>{memberSinceDate}</p>
              </div>
            </div>
          </div>
        </div>

        {/* --- Donation History (Right Column) --- */}
        <div className="col-lg-4">
          <div className="card text-white bg-dark shadow p-4">
            <h5 className="card-title mb-3">Donation History</h5>
            <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
              {profile.history && profile.history.length > 0 ? (
                profile.history
                  .sort((a, b) => new Date(b.donation_date) - new Date(a.donation_date))
                  .map((h, index) => (
                    <div key={h._id || index} className={`p-2 rounded ${index > 0 ? 'mt-2 border-top border-secondary' : ''}`}>
                      <p className="mb-0 fw-bold">{h.venue}</p>
                      <small className="text-muted">
                        {formatDate(h.donation_date)} - {h.units || 1} unit(s)
                      </small>
                    </div>
                  ))
              ) : (
                <p className="text-muted">No donation history recorded yet.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
