import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function AdminLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('isAdmin', 'true');
        alert(data.message);
        navigate('/admin/dashboard'); // Redirect to the admin dashboard
      } else {
        alert(data.message);
      }
    } catch (error) {
      alert('Admin login failed.');
    }
  };

  return (
    <div className="row justify-content-center">
      <div className="col-lg-5 col-md-7">
        <div className="card shadow-sm p-4 p-md-5">
          <div className="text-center mb-4">
            <h2 className="fw-bold">Admin Login</h2>
            <p className="text-muted">Access the LifeLine control panel.</p>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Username</label>
              <input type="text" className="form-control" id="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Password</label>
              <input type="password" className="form-control" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
            </div>
            <div className="d-grid mt-4">
              <button type="submit" className="btn btn-dark btn-lg">Login</button>
            </div>
             <div className="text-center mt-3">
              <p className="text-muted small"><Link to="/login" className="text-secondary">Return to User Login</Link></p>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
export default AdminLogin;
