import React, { useState } from 'react';

function RequestBlood() {
  const [formData, setFormData] = useState({
    patient_name: '', blood_group: '', units_required: '', hospital_name: '',
    contact_person: '', contact_mobile: '', message: ''
  });

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/request-blood', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      alert(data.message);
      if(response.ok) e.target.reset(); // Clear the form on successful submission
    } catch (error) {
      alert('Request submission failed.');
    }
  };

  return (
    <div>
      <h2>Request for Blood</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="patient_name" placeholder="Patient Name" onChange={handleChange} required />
        <select name="blood_group" onChange={handleChange} required>
          <option value="">Select Blood Group</option>
           <option>A+</option><option>A-</option><option>B+</option><option>B-</option>
          <option>AB+</option><option>AB-</option><option>O+</option><option>O-</option>
        </select>
        <input type="number" name="units_required" placeholder="Units Required" onChange={handleChange} required />
        <input type="text" name="hospital_name" placeholder="Hospital Name" onChange={handleChange} required />
        <input type="text" name="contact_person" placeholder="Contact Person" onChange={handleChange} required />
        <input type="text" name="contact_mobile" placeholder="Contact Mobile" onChange={handleChange} required />
        <textarea name="message" placeholder="Additional Message" onChange={handleChange}></textarea>
        <button type="submit">Submit Request</button>
      </form>
    </div>
  );
}
export default RequestBlood;
