import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function StockReport() {
  const [stock, setStock] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (localStorage.getItem('isAdmin') !== 'true') {
        navigate('/admin/login');
    } else {
        const fetchStock = async () => {
            const response = await fetch('http://localhost:5000/api/admin/stock');
            const data = await response.json();
            setStock(data);
        };
        fetchStock();
    }
  }, [navigate]);

  const handleUnitChange = (index, value) => {
    const updatedStock = [...stock];
    // Ensure the value is not negative
    updatedStock[index].units = Math.max(0, parseInt(value, 10) || 0);
    setStock(updatedStock);
  };

  const handleUpdateStock = async () => {
      await fetch('http://localhost:5000/api/admin/stock', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(stock)
      });
      alert('Stock updated!');
  };

  return (
    <div>
      <h2>Blood Stock Report</h2>
      <table>
        <thead><tr><th>Blood Group</th><th>Units</th></tr></thead>
        <tbody>
          {stock.map((item, index) => (
            <tr key={item._id}>
              <td>{item.blood_group}</td>
              <td><input type="number" value={item.units} onChange={(e) => handleUnitChange(index, e.target.value)} /></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleUpdateStock}>Update Stock</button>
    </div>
  );
}
export default StockReport;
