import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';

function Profile() {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();
  // Assume userId is the correct key used for storage
  const userId = localStorage.getItem('userId');

  // Function to format the date
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  // Function to determine the last donation date
  const getLastDonationDate = (history) => {
    if (!history || history.length === 0) return 'Never';
    
    // Sort history by date descending
    const sortedHistory = [...history].sort((a, b) => 
        new Date(b.donation_date) - new Date(a.donation_date)
    );
    return formatDate(sortedHistory[0].donation_date);
  };

  // useEffect runs automatically when the component first loads
  useEffect(() => {
    if (!userId) {
      alert("Please log in to view your profile.");
      navigate('/login');
      return;
    }
    
    // NOTE: Ensure your backend endpoint returns { user: {...}, history: [...] }
    const fetchProfile = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/profile/${userId}`);
        if (!response.ok) throw new Error('Failed to fetch profile data');

        const data = await response.json();
        setProfile(data); // Save the fetched data to state
      } catch (error) {
        console.error("Error fetching profile:", error);
        // Handle error, e.g., redirect or show an error message
        // navigate('/login'); 
      }
    };
    fetchProfile();
  }, [userId, navigate]); // The effect depends on userId and navigate

  if (!profile || !profile.user) return <div>Loading profile...</div>; // Show a loading message until data is fetched

  // Calculate Last Donation Date
  const lastDonationDate = getLastDonationDate(profile.history);

  // --- Rendering the Profile UI ---
  return (
    <div className="container py-4">
      <h2 className="fw-bold mb-4">My Profile</h2>
      <div className="row g-4">
        {/* --- Personal Information (Left Column) --- */}
        <div className="col-lg-8">
          <div className="card shadow-sm p-4">
            <div className="d-flex justify-content-between align-items-center mb-4 border-bottom pb-3">
              <h5 className="mb-0 text-muted">Personal Information</h5>
              <button className="btn btn-outline-danger btn-sm" onClick={() => navigate('/edit-profile')}>
                <i className="bi bi-pencil me-1"></i> Edit Profile
              </button>
            </div>
            
            <div className="row g-3">
              {/* Row 1: Full Name and Email */}
              <div className="col-md-6">
                <p className="fw-bold mb-0">Full Name:</p>
                <p>{profile.user.name}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Email Address:</p>
                <p>{profile.user.email}</p>
              </div>

              {/* Row 2: Age and Blood Group */}
              <div className="col-md-6">
                <p className="fw-bold mb-0">Age:</p>
                <p>{profile.user.age || 'N/A'}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0 text-danger">Blood Group:</p>
                <p className="fw-bold text-danger">{profile.user.blood_group || 'N/A'}</p>
              </div>

              {/* Row 3: Gender and Mobile Number */}
              <div className="col-md-6">
                <p className="fw-bold mb-0">Gender:</p>
                <p>{profile.user.gender || 'N/A'}</p>
              </div>
              <div className="col-md-6">
                <p className="fw-bold mb-0">Mobile Number:</p>
                <p>{profile.user.mobile || 'N/A'}</p>
              </div>

              {/* Row 4: Address (Full Width) */}
              <div className="col-12 mt-3">
                <p className="fw-bold mb-0">Address:</p>
                {/* NOTE: If your backend returns address components, you might need to combine them here */}
                <p>{profile.user.address || 'Address not set'}</p>
                <Link to={`https://maps.google.com/?q=${encodeURIComponent(profile.user.address)}`} target="_blank" className="btn btn-outline-secondary btn-sm">
                  View on Map
                </Link>
              </div>
              
              {/* Row 5: Last Donation Date and Member Since */}
              <div className="col-md-6 mt-3">
                <p className="fw-bold mb-0">Last Donation Date:</p>
                <p>{lastDonationDate}</p>
              </div>
              <div className="col-md-6 mt-3">
                <p className="fw-bold mb-0">Member Since:</p>
                <p>{formatDate(profile.user.member_since) || 'N/A'}</p>
              </div>
            </div>
          </div>
        </div>

        {/* --- Donation History (Right Column) --- */}
        <div className="col-lg-4">
          <div className="card text-white bg-dark shadow p-4">
            <h5 className="card-title mb-3">Donation History</h5>
            <div style={{ maxHeight: '300px', overflowY: 'auto' }}>
              {profile.history && profile.history.length > 0 ? (
                profile.history
                  .sort((a, b) => new Date(b.donation_date) - new Date(a.donation_date)) // Sort newest first
                  .map((h, index) => (
                    <div key={h._id || index} className={`p-2 rounded ${index > 0 ? 'mt-2 border-top border-secondary' : ''}`}>
                      <p className="mb-0 fw-bold">{h.venue}</p>
                      <small className="text-muted">
                        {formatDate(h.donation_date)} - {h.units || 1} unit(s)
                      </small>
                    </div>
                  ))
              ) : (
                <p className="text-muted">No donation history recorded yet.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
