import React from 'react';

function WhyDonate() {
  return (
    <div className="page-content">
      <h2>Why Your Donation Matters</h2>
      <p>A single donation can save up to three lives. Blood is essential for surgeries, cancer treatment, chronic illnesses, and traumatic injuries. Your contribution is a lifeline for someone in need.</p>
      
      
    </div>
  );
}
export default WhyDonate;
